import org.apache.nifi.flowfile.FlowFile
import java.time.LocalDate
import java.time.format.DateTimeFormatter

def flowFile = session.get()
if (!flowFile) return

// Data pocztkowa (staa)
def startDate = "2025-12-01T00:00:00Z"

// Data kocowa = wczoraj (dane daily s za poprzedni dzie)
def yesterday = LocalDate.now().minusDays(1)
def endDate = yesterday.format(DateTimeFormatter.ISO_LOCAL_DATE) + "T23:59:59Z"

// Tworzenie FlowFile z zakresem dat
def newFF = session.clone(flowFile)
newFF = session.putAttribute(newFF, "date_start", startDate)
newFF = session.putAttribute(newFF, "date_end", endDate)
session.transfer(newFF, REL_SUCCESS)

session.remove(flowFile)
